import React, { useState, useMemo } from 'react';
import { FiFileText, FiHome, FiDollarSign, FiTarget, FiSettings, FiPlusCircle, FiFilter, FiMoreVertical, FiBell, FiLifeBuoy, FiBookOpen, FiPenTool, FiCalendar, FiFilm, FiShoppingBag, FiX, FiHeart, FiSearch, FiTag, FiShoppingCart, FiStar, FiClock, FiList, FiPlus, FiEdit3 } from 'react-icons/fi';
import { IconType } from 'react-icons';

interface Urun {
  id: number;
  isim: string;
  fiyat: number;
  kategori: string;
  etiketler: string[];
  resim: string;
  aciklama: string;
  tercihEdilen?: boolean;
  zamanPlanlamasi?: 'bu-ay' | 'bu-yil' | 'alınacaklar' | 'ihtiyaç';
  sepetler?: string[];
}

interface Sepet {
  id: string;
  isim: string;
  urunler: number[];
  olusturmaTarihi: Date;
}

interface KartProps {
  baslik: string;
  children: React.ReactNode;
  className?: string;
  headerAction?: React.ReactNode;
}

interface ButonProps {
  children: React.ReactNode;
  onClick?: (e?: React.MouseEvent) => void;
  variant?: 'primary' | 'secondary' | 'warning';
  icon?: IconType;
  className?: string;
  type?: 'button' | 'submit' | 'reset';
}

interface ModalProps {
  isOpen: boolean;
  onClose: () => void;
  baslik: string;
  children: React.ReactNode;
}

interface UrunKartiProps {
  urun: Urun;
  onDetailsClick: (urun: Urun) => void;
  onTercihToggle: (urunId: number) => void;
  onZamanPlanlamasiChange: (urunId: number, plan: Urun['zamanPlanlamasi']) => void;
  onSepeteEkle: (urunId: number, sepetId?: string) => void;
  sepetler: Sepet[];
  onCreateSepet: (isim: string) => void;
}

interface UrunDetayModalProps {
  isOpen: boolean;
  onClose: () => void;
  urun: Urun | null;
  onTercihToggle: (urunId: number) => void;
  onZamanPlanlamasiChange: (urunId: number, plan: Urun['zamanPlanlamasi']) => void;
  onSepeteEkle: (urunId: number, sepetId?: string) => void;
  sepetler: Sepet[];
  onCreateSepet: (isim: string) => void;
}

interface SepetOlusturModalProps {
  isOpen: boolean;
  onClose: () => void;
  onCreateSepet: (isim: string) => void;
}

const renkler = {
  kartBaslik: '#e9ecef',
  buton: '#112e4b',
  logoBaslik: '#6c8db1',
  anaYazi: '#212529',
  acikYazi: '#7b8ab8',
  transparanYazi: '#212529bf',
  uyari: '#ffc107',
  arkaplan: '#f8f9fa',
};

const ornekUrunler: Urun[] = [
    { id: 1, isim: 'Logitech MX Master 3S', fiyat: 2999, kategori: 'Elektronik', etiketler: ['fare', 'ofis', 'kablosuz'], resim: 'https://placehold.co/600x400/e9ecef/212529?text=MX+Master+3S', aciklama: 'Gelişmiş kablosuz performans ve ergonomi sunan profesyonel fare.', tercihEdilen: true, zamanPlanlamasi: 'bu-ay' },
