import React, { useState, useMemo } from 'react';
import { FiFileText, FiHome, FiDollarSign, FiTarget, FiSettings, FiPlusCircle, FiFilter, FiMoreVertical, FiBell, FiLifeBuoy, FiBookOpen, FiPenTool, FiCalendar, FiFilm, FiShoppingBag, FiX, FiHeart, FiSearch, FiTag, FiShoppingCart } from 'react-icons/fi';
import { IconType } from 'react-icons';

interface Urun {
  id: number;
  isim: string;
  fiyat: number;
  kategori: string;
  etiketler: string[];
  resim: string;
  aciklama: string;
}

interface KartProps {
  baslik: string;
  children: React.ReactNode;
  className?: string;
  headerAction?: React.ReactNode;
}

interface ButonProps {
  children: React.ReactNode;
  onClick?: () => void;
  variant?: 'primary' | 'secondary' | 'warning';
  icon?: IconType;
  className?: string;
}

interface ModalProps {
  isOpen: boolean;
  onClose: () => void;
  baslik: string;
  children: React.ReactNode;
}

interface UrunKartiProps {
  urun: Urun;
  onDetailsClick: (urun: Urun) => void;
}

interface UrunDetayModalProps {
  isOpen: boolean;
  onClose: () => void;
  urun: Urun | null;
}

const renkler = {
  kartBaslik: '#e9ecef',
  buton: '#112e4b',
  logoBaslik: '#6c8db1',
  anaYazi: '#212529',
  acikYazi: '#7b8ab8',
  transparanYazi: '#212529bf',
  uyari: '#ffc107',
  arkaplan: '#f8f9fa',
};

const ornekUrunler: Urun[] = [
    { id: 1, isim: 'Logitech MX Master 3S', fiyat: 2999, kategori: 'Elektronik', etiketler: ['fare', 'ofis', 'kablosuz'], resim: 'https://placehold.co/600x400/e9ecef/212529?text=MX+Master+3S', aciklama: 'Gelişmiş kablosuz performans ve ergonomi sunan profesyonel fare.' },
    { id: 2, isim: 'Sony WH-1000XM5 Kulaklık', fiyat: 8500, kategori: 'Elektronik', etiketler: ['kulaklık', 'müzik', 'gürültü engelleme'], resim: 'https://placehold.co/600x400/e9ecef/212529?text=Sony+XM5', aciklama: 'Sektör lideri gürültü engelleme özelliğine sahip yüksek kaliteli kablosuz kulaklık.' },
    { id: 3, isim: 'Nespresso Lattissima One', fiyat: 6200, kategori: 'Mutfak', etiketler: ['kahve', 'kapsül', 'makine'], resim: 'https://placehold.co/600x400/e9ecef/212529?text=Nespresso', aciklama: 'Tek tuşla taze süt köpüklü kahveler hazırlayan kompakt ve şık kahve makinesi.' },
    { id: 4, isim: 'Apple MacBook Air M2', fiyat: 38000, kategori: 'Elektronik', etiketler: ['laptop', 'apple', 'taşınabilir'], resim: 'https://placehold.co/600x400/e9ecef/212529?text=MacBook+Air', aciklama: 'İnanılmaz ince ve hafif tasarımda güçlü M2 çip ile üstün performans.' },
    { id: 5, isim: 'Samsung Odyssey G9 Monitör', fiyat: 35000, kategori: 'Elektronik', etiketler: ['monitör', 'oyuncu', 'geniş ekran'], resim: 'https://placehold.co/600x400/e9ecef/212529?text=Odyssey+G9', aciklama: 'Sürükleyici bir oyun deneyimi için 49 inç kavisli QLED oyun monitörü.' },
    { id: 6, isim: 'Anker PowerCore 20000', fiyat: 1500, kategori: 'Aksesuar', etiketler: ['powerbank', 'mobil', 'şarj'], resim: 'https://placehold.co/600x400/e9ecef/212529?text=Anker', aciklama: 'Yüksek kapasiteli ve hızlı şarj özellikli taşınabilir şarj cihazı.' },
    { id: 7, isim: 'Stanley Termos 1L', fiyat: 1800, kategori: 'Outdoor', etiketler: ['termos', 'kamp', 'içecek'], resim: 'https://placehold.co/600x400/e9ecef/212529?text=Stanley', aciklama: 'İçecekleri saatlerce sıcak veya soğuk tutan ikonik ve dayanıklı termos.' },
    { id: 8, isim: 'Xiaomi Mi Robot Vacuum', fiyat: 9000, kategori: 'Ev Aletleri', etiketler: ['robot süpürge', 'temizlik', 'akıllı ev'], resim: 'https://placehold.co/600x400/e9ecef/212529?text=Robot+Süpürge', aciklama: 'Akıllı haritalama ve otomatik şarj özellikli robot süpürge ve paspas.' },
];

const Kart: React.FC<KartProps> = ({ baslik, children, className = '', headerAction }) => (
  <div className={`bg-white rounded-lg shadow-md overflow-hidden ${className}`}>
    <div className="p-4 flex justify-between items-center" style={{ backgroundColor: renkler.kartBaslik, borderBottom: '1px solid #dee2e6' }}>
      <h3 className="font-bold text-lg" style={{ color: renkler.anaYazi }}>{baslik}</h3>
      {headerAction}
    </div>
    <div className="p-4 md:p-6">{children}</div>
  </div>
);

const Buton: React.FC<ButonProps> = ({ children, onClick, variant = 'primary', icon: Icon, className = '' }) => {
  const baseClasses = 'px-4 py-2 rounded-lg font-semibold flex items-center justify-center transition-transform transform hover:scale-105 disabled:opacity-50 disabled:scale-100';
  const styles: Record<string, string> = {
    primary: `bg-[${renkler.buton}] text-white`,
    secondary: `bg-gray-200 text-[${renkler.anaYazi}]`,
    warning: `bg-[${renkler.uyari}] text-[${renkler.anaYazi}]`,
  };
  return (
    <button onClick={onClick} className={`${baseClasses} ${styles[variant]} ${className}`}>
      {Icon && <Icon className="mr-2 h-4 w-4" />}
      {children}
    </button>
  );
};

const Modal: React.FC<ModalProps> = ({ isOpen, onClose, baslik, children }) => {
    if (!isOpen) return null;
    return (
        <div className="fixed inset-0 bg-black bg-opacity-60 z-50 flex justify-center items-center p-4" onClick={onClose}>
            <div className="bg-white rounded-lg shadow-xl w-full max-w-4xl max-h-[90vh] flex flex-col transform transition-all" onClick={e => e.stopPropagation()}>
                <div className="p-4 border-b flex justify-between items-center">
                    <h2 className="text-xl font-bold" style={{ color: renkler.anaYazi }}>{baslik}</h2>
                    <button onClick={onClose} className="text-gray-500 hover:text-gray-800 p-2 rounded-full hover:bg-gray-200"><FiX size={20}/></button>
                </div>
                <div className="p-6 overflow-y-auto">{children}</div>
            </div>
        </div>
    );
};

const UrunKarti: React.FC<UrunKartiProps> = ({ urun, onDetailsClick }) => (
    <div className="bg-white rounded-lg shadow-md overflow-hidden group transition-all duration-300 hover:shadow-xl hover:-translate-y-1">
        <div className="relative">
            <img src={urun.resim} alt={urun.isim} className="w-full h-48 object-cover"/>
            <div className="absolute top-2 right-2 bg-white/80 backdrop-blur-sm p-2 rounded-full opacity-0 group-hover:opacity-100 transition-opacity">
                <button className="text-red-500 hover:text-red-700">
                    <FiHeart size={20} />
                </button>
            </div>
        </div>
        <div className="p-4">
            <p className="text-sm font-semibold" style={{ color: renkler.acikYazi }}>{urun.kategori}</p>
            <h4 className="text-lg font-bold truncate" style={{ color: renkler.anaYazi }}>{urun.isim}</h4>
            <p className="text-xl font-black mt-2" style={{ color: renkler.buton }}>
                {urun.fiyat.toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}
            </p>
            <div className="mt-4 flex space-x-2">
                <Buton onClick={() => onDetailsClick(urun)} className="w-full" variant="secondary">Detaylar</Buton>
                <Buton onClick={() => {}} className="w-full">Sepete Ekle</Buton>
            </div>
        </div>
    </div>
);

const UrunDetayModal: React.FC<UrunDetayModalProps> = ({ isOpen, onClose, urun }) => {
    if (!urun) return null;
    const benzerUrunler = ornekUrunler.filter(p => p.kategori === urun.kategori && p.id !== urun.id).slice(0, 3);
    return (
        <Modal isOpen={isOpen} onClose={onClose} baslik={urun.isim}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                <div>
                    <img src={urun.resim} alt={urun.isim} className="w-full h-auto object-cover rounded-lg shadow-lg"/>
                </div>
                <div>
                    <p className="text-sm font-semibold mb-2" style={{color: renkler.buton}}>{urun.kategori}</p>
                    <p className="mb-4" style={{color: renkler.anaYazi}}>{urun.aciklama}</p>
                    <div className="mb-4">
                        {urun.etiketler.map((etiket: string) => (
                            <span key={etiket} className="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold mr-2 mb-2" style={{color: renkler.anaYazi}}>#{etiket}</span>
                        ))}
                    </div>
                    <p className="text-3xl font-extrabold my-4" style={{ color: renkler.buton }}>
                        {urun.fiyat.toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}
                    </p>
                    <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-3">
                        <Buton icon={FiShoppingCart} onClick={() => {}} className="w-full">Sepete Ekle</Buton>
                        <Buton icon={FiHeart} variant="secondary" onClick={() => {}} className="w-full">Favorilere Ekle</Buton>
                    </div>
                </div>
            </div>
             <div className="mt-10">
                <h3 className="text-xl font-bold mb-4" style={{color: renkler.anaYazi}}>Benzer Ürünler</h3>
                 <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                    {benzerUrunler.map(p => (
                        <div key={p.id} className="border rounded-lg p-3 flex items-center space-x-3 hover:bg-gray-50">
                            <img src={p.resim} alt={p.isim} className="w-16 h-16 object-cover rounded-md"/>
                            <div>
                                <p className="font-semibold text-sm">{p.isim}</p>
                                <p className="text-xs" style={{color: renkler.acikYazi}}>{p.fiyat.toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </Modal>
    );
};

const FavoriUrunlerPage: React.FC = () => {
    const [arama, setArama] = useState('');
    const [kategori, setKategori] = useState('hepsi');
    const maxFiyat = useMemo(() => Math.max(...ornekUrunler.map(p => p.fiyat)), []);
    const [fiyatAralik, setFiyatAralik] = useState(maxFiyat);
    const [seciliUrun, setSeciliUrun] = useState<Urun | null>(null);

    const kategoriler = useMemo(() => ['hepsi', ...Array.from(new Set(ornekUrunler.map(p => p.kategori)))], []);

    const filtreliUrunler = useMemo(() => {
        return ornekUrunler.filter(p => {
            const aramaUygun = p.isim.toLowerCase().includes(arama.toLowerCase());
            const kategoriUygun = kategori === 'hepsi' || p.kategori === kategori;
            const fiyatUygun = p.fiyat <= fiyatAralik;
            return aramaUygun && kategoriUygun && fiyatUygun;
        });
    }, [arama, kategori, fiyatAralik]);
    
    const detayTikla = (urun: Urun) => {
        setSeciliUrun(urun);
    };

    const modalKapat = () => {
        setSeciliUrun(null);
    };

    return (
        <div className="space-y-6">
            <h2 className="text-3xl font-bold" style={{ color: renkler.anaYazi }}>Favori Ürünler</h2>

            {/* Filtreleme Alanı */}
            <Kart baslik="Filtrele" headerAction={<Buton variant="secondary" onClick={() => {setArama(''); setKategori('hepsi'); setFiyatAralik(maxFiyat)}} className="">Temizle</Buton>}>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {/* Arama */}
                    <div className="relative">
                        <input type="text" placeholder="Ürün ara..." value={arama} onChange={e => setArama(e.target.value)} className="w-full p-2 pl-10 border rounded-md"/>
                        <FiSearch className="absolute left-3 top-1/2 -translate-y-1/2" size={18} style={{color: renkler.acikYazi}} />
                    </div>
                    {/* Kategori */}
                    <select value={kategori} onChange={e => setKategori(e.target.value)} className="w-full p-2 border rounded-md bg-white">
                        {kategoriler.map(cat => <option key={cat} value={cat}>{cat === 'hepsi' ? 'Tüm Kategoriler' : cat}</option>)}
                    </select>
                    {/* Fiyat Aralığı */}
                    <div className="flex flex-col justify-center">
                        <label className="mb-1 text-sm font-medium">Fiyat Aralığı (Max: {fiyatAralik.toLocaleString('tr-TR')} ₺)</label>
                        <input type="range" min="0" max={maxFiyat} value={fiyatAralik} onChange={e => setFiyatAralik(Number(e.target.value))} className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer" />
                    </div>
                </div>
            </Kart>

            {/* Ürün Grid'i */}
            {filtreliUrunler.length > 0 ? (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                    {filtreliUrunler.map(urun => (
                        <UrunKarti key={urun.id} urun={urun} onDetailsClick={detayTikla} />
                    ))}
                </div>
            ) : (
                <div className="text-center py-16">
                    <p className="text-xl font-semibold" style={{color: renkler.anaYazi}}>Aradığınız kriterlere uygun ürün bulunamadı.</p>
                    <p style={{color: renkler.acikYazi}}>Lütfen filtrelerinizi değiştirmeyi deneyin.</p>
                </div>
            )}
            
            <UrunDetayModal isOpen={!!seciliUrun} onClose={modalKapat} urun={seciliUrun} />
        </div>
    );
};

export default FavoriUrunlerPage;
