import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

// Mock data, will be replaced by props or API calls
const category_expenses = [
    { category_type: 'Gıda', total_amount: 500 },
    { category_type: 'Ulaşım', total_amount: 150 },
    { category_type: 'Eğlence', total_amount: 200 },
    { category_type: 'Fatura', total_amount: 300 },
    { category_type: 'Diğer', total_amount: 100 },
];

const recent_transactions = [
    { icon: 'bi-receipt', description: 'Market Alışverişi', date: '08.08.2025', amount: -500.00 },
    { icon: 'bi-building', description: 'Depozito', date: '08.08.2025', amount: -24000.00 },
    { icon: 'bi-house-door', description: 'Kira', date: '07.08.2025', amount: -24000.00 },
    { icon: 'bi-key', description: 'Emlak Komisyonu', date: '07.08.2025', amount: -34560.00 },
];

const Charts: React.FC = () => {
    const chartRef = useRef<HTMLCanvasElement>(null);
    const chartInstance = useRef<Chart | null>(null);

    useEffect(() => {
        if (chartRef.current) {
            if (chartInstance.current) {
                chartInstance.current.destroy();
            }
            const ctx = chartRef.current.getContext('2d');
            if (ctx) {
                const labels = category_expenses.map(item => item.category_type);
                const data = category_expenses.map(item => item.total_amount);
                const colors = [
                    '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0',
                    '#9966FF', '#FF9F40', '#FF6384', '#C9CBCF'
                ];
                chartInstance.current = new Chart(ctx, {
                    type: 'doughnut',
                    data: {
                        labels: labels,
                        datasets: [{
                            label: 'Toplam Harcama',
                            data: data,
                            backgroundColor: colors,
                            borderColor: '#fff',
                            borderWidth: 2,
                            hoverOffset: 4
                        }]
                    },
                    options: {
                        responsive: true,
                        maintainAspectRatio: false,
                        plugins: {
                            legend: {
                                position: 'bottom',
                                labels: {
                                    padding: 20,
                                    usePointStyle: true,
                                    font: {
                                        size: 12
                                    }
                                }
                            },
                            tooltip: {
                                callbacks: {
                                    label: function (context) {
                                        const value = context.parsed;
                                        const total = context.dataset.data.reduce((a, b) => a + b, 0);
                                        const percentage = ((value / total) * 100).toFixed(1);
                                        return `${context.label}: ₺${value.toLocaleString('tr-TR')} (${percentage}%)`;
                                    }
                                }
                            }
                        },
                        animation: {
                            animateScale: true,
                            animateRotate: true
                        }
                    }
                });
            }
        }
    }, []);

    return (
        <div className="row mb-4">
            {/* Expense Analysis Chart */}
            <div className="col-xl-8 mb-4">
                <div className="card modern-card" data-aos="fade-up">
                    <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="card-title mb-0">Kategoriye Göre Gider Dağılımı</h5>
                        <div className="dropdown">
                            <button className="btn btn-sm btn-outline-secondary dropdown-toggle" type="button"
                                data-bs-toggle="dropdown">
                                Bu Ay
                            </button>
                            <ul className="dropdown-menu">
                                <li><a className="dropdown-item" href="#">Bu Ay</a></li>
                                <li><a className="dropdown-item" href="#">Son 3 Ay</a></li>
                                <li><a className="dropdown-item" href="#">Bu Yıl</a></li>
                            </ul>
                        </div>
                    </div>
                    <div className="card-body">
                        <canvas id="categoryExpenseChart" ref={chartRef} height="300"></canvas>
                    </div>
                </div>
            </div>

            {/* Recent Transactions */}
            <div className="col-xl-4 mb-4">
                <div className="card modern-card recent-transactions h-100" data-aos="fade-up" data-aos-delay="100">
                    <div className="card-header d-flex justify-content-between align-items-center">
                        <h5 className="card-title mb-0">Son İşlemler</h5>
                        <a href="/expense" className="btn btn-sm btn-link">Tümünü Gör</a>
                    </div>
                    <div className="card-body">
                        <div style={{display: 'flex', flexDirection: 'column', gap: 12}}>
                            {recent_transactions.map((transaction, index) => (
                                <div key={index} style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between'}}>
                                    <div style={{display: 'flex', alignItems: 'center', gap: 12}}>
                                        <div style={{width: 40, height: 40, background: '#ffebee', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                                            <i className={`bi ${transaction.icon}`} style={{color: '#e74c3c', fontSize: 20}}></i>
                                        </div>
                                        <div>
                                            <span>{transaction.description}</span>
                                            <div style={{fontSize: 13, color: '#888'}}>{transaction.date}</div>
                                        </div>
                                    </div>
                                    <span style={{color: '#e74c3c', fontWeight: 600}}>
                                        {transaction.amount.toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}
                                    </span>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Charts;
