import React, { useEffect, useState } from 'react';

const Footer: React.FC = () => {
  const [left, setLeft] = useState('80px');

  useEffect(() => {
    const updateLeft = () => {
      if (window.innerWidth >= 640) {
        setLeft('224px'); // sm: w-56
      } else {
        setLeft('80px'); // w-20
      }
    };
    updateLeft();
    window.addEventListener('resize', updateLeft);
    return () => window.removeEventListener('resize', updateLeft);
  }, []);

  return (
    <footer
      className="fixed bottom-0 z-50 bg-[#191923] text-white flex items-center justify-between px-8 py-4 transition-all duration-300"
      style={{ left, right: 0, width: 'auto', minWidth: 0, maxWidth: '100vw' }}
    >
      <div className="text-2xl font-bold">Logo</div>
      <div className="text-center flex-1">
        <span className="text-sm">Â© 2023 <span className="font-bold">Lorem Ipsum</span>. All Rights Reserved.</span>
      </div>
      <div className="flex items-center gap-6 text-xl">
        <a href="#" aria-label="Facebook"><i className="fab fa-facebook-f"></i></a>
        <a href="#" aria-label="Twitter"><i className="fab fa-twitter"></i></a>
        <a href="#" aria-label="Instagram"><i className="fab fa-instagram"></i></a>
        <a href="#" aria-label="YouTube"><i className="fab fa-youtube"></i></a>
      </div>
    </footer>
  );
};

export default Footer;
