import React from 'react';
import { FiGrid, FiFileText, FiShoppingCart, FiCreditCard, FiKey, FiFolder, FiBook, FiSettings, FiBanknote } from 'react-icons/fi';

const features = [
  {
    title: 'Gider Yönetimi',
    desc: 'Harcama takibi',
    icon: <FiFileText className="text-3xl mb-2" />,
    href: '/expense',
    border: 'border-[#1e293b]',
    color: 'text-[#1e293b]',
  },
  {
    title: 'Alınacaklar',
    desc: 'Wishlist yönetimi',
    icon: <FiShoppingCart className="text-3xl mb-2" />,
    href: '/wishlist',
    border: 'border-yellow-400',
    color: 'text-yellow-500',
    selected: true,
  },
  {
    title: 'Borç Takibi',
    desc: 'Vergi, SGK, İcra',
    icon: <FiCreditCard className="text-3xl mb-2" />,
    href: '/tax',
    border: 'border-[#1e293b]',
    color: 'text-[#1e293b]',
  },
  {
    title: 'Banka Hesapları',
    desc: 'IBAN yönetimi',
    icon: <FiBanknote className="text-3xl mb-2" />,
    href: '/bankaccount',
    border: 'border-[#1e293b]',
    color: 'text-[#1e293b]',
  },
  {
    title: 'Hesap Şifreleri',
    desc: 'Güvenli saklama',
    icon: <FiKey className="text-3xl mb-2" />,
    href: '/accountpassword',
    border: 'border-[#1e293b]',
    color: 'text-[#1e293b]',
  },
  {
    title: 'Projeler',
    desc: 'Proje yönetimi',
    icon: <FiFolder className="text-3xl mb-2" />,
    href: '/project',
    border: 'border-[#1e293b]',
    color: 'text-[#1e293b]',
  },
  {
    title: 'Notlar',
    desc: 'Not defteri',
    icon: <FiBook className="text-3xl mb-2" />,
    href: '/note',
    border: 'border-[#1e293b]',
    color: 'text-[#1e293b]',
  },
  {
    title: 'Ayarlar',
    desc: 'Sistem ayarları',
    icon: <FiSettings className="text-3xl mb-2" />,
    href: '/settings',
    border: 'border-[#1e293b]',
    color: 'text-[#1e293b]',
  },
];

const AllFeatures: React.FC = () => {
  return (
    <div className="bg-white rounded-xl shadow-md p-6 mb-6">
      <div className="flex justify-between items-center mb-6 border-b border-gray-200 pb-4">
        <h3 className="text-lg font-bold text-gray-900 m-0 tracking-tight flex items-center gap-2">
          <FiGrid className="text-blue-500" />Tüm Özellikler
        </h3>
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {features.map((f, i) => (
          <a
            key={f.title}
            href={f.href}
            className={`flex flex-col items-center justify-center border-2 rounded-xl p-8 transition-all duration-200 hover:border-blue-400 hover:shadow-lg text-center bg-white ${f.selected ? 'border-yellow-400' : f.border}`}
            style={f.selected ? { color: '#eab308', background: '#fefce8', fontWeight: 600 } : {}}
          >
            <span className={`mb-2 ${f.selected ? 'text-yellow-500' : f.color}`}>{f.icon}</span>
            <span className={`font-bold text-lg mb-1 ${f.selected ? 'text-yellow-500' : 'text-[#1e293b]'}`}>{f.title}</span>
            <span className="text-gray-500 text-sm">{f.desc}</span>
          </a>
        ))}
      </div>
    </div>
  );
};

export default AllFeatures;
