import React, { useMemo } from 'react';
import { FiEye, FiDownload, FiEdit, FiTrash2 } from 'react-icons/fi';

// İcra dosyaları veri tipi
interface IcraDosyasi {
  id: number;
  tarafRolu: string;
  mahkemeAdi: string;
  esasNo: string;
  dosyaTuru: string;
  acilisTarihi: string;
  dosyaDurumu: string;
  tutar?: number;
  aciklama?: string;
}

// Örnek veriler
const icraVerileri: IcraDosyasi[] = [
  {
    id: 1,
    tarafRolu: 'Borçlu',
    mahkemeAdi: 'Adana 3. Genel İcra Dairesi',
    esasNo: '2025/27541',
    dosyaTuru: 'İcra Dosyası',
    acilisTarihi: '2025-04-18',
    dosyaDurumu: 'Kapalı (Takipsizlik)',
    tutar: 15000,
    aciklama: 'Kredi borcu takibi'
  },
  {
    id: 2,
    tarafRolu: 'Borçlu',
    mahkemeAdi: 'Karşıyaka 4. İcra Dairesi',
    esasNo: '2025/2945',
    dosyaTuru: 'İcra Dosyası',
    acilisTarihi: '2025-04-16',
    dosyaDurumu: 'Açık',
    tutar: 8500,
    aciklama: 'Kira borcu takibi'
  },
  {
    id: 3,
    tarafRolu: 'Borçlu',
    mahkemeAdi: 'Merkezi Takip Sistemi',
    esasNo: '2025/271868',
    dosyaTuru: 'MTS',
    acilisTarihi: '2025-03-18',
    dosyaDurumu: 'Açık',
    tutar: 25000,
    aciklama: 'Vergi borcu takibi'
  },
  {
    id: 4,
    tarafRolu: 'Borçlu',
    mahkemeAdi: 'İstanbul 28. İcra Dairesi',
    esasNo: '2025/1180',
    dosyaTuru: 'İcra Dosyası',
    acilisTarihi: '2025-01-13',
    dosyaDurumu: 'Açık',
    tutar: 12000,
    aciklama: 'Fatura borcu takibi'
  },
  {
    id: 5,
    tarafRolu: 'Borçlu',
    mahkemeAdi: 'İstanbul 35. İcra Dairesi',
    esasNo: '2024/27011',
    dosyaTuru: 'İcra Dosyası',
    acilisTarihi: '2024-10-14',
    dosyaDurumu: 'Açık',
    tutar: 18000,
    aciklama: 'Kredi kartı borcu takibi'
  },
  {
    id: 6,
    tarafRolu: 'Borçlu',
    mahkemeAdi: 'Tarsus İcra Dairesi',
    esasNo: '2024/2800',
    dosyaTuru: 'Talimat Dosyası',
    acilisTarihi: '2024-10-11',
    dosyaDurumu: 'Açık',
    tutar: 9500,
    aciklama: 'Alacak takibi'
  },
  {
    id: 7,
    tarafRolu: 'Borçlu',
    mahkemeAdi: 'İzmir 18. İcra Dairesi',
    esasNo: '2022/10723',
    dosyaTuru: 'İcra Dosyası',
    acilisTarihi: '2022-08-18',
    dosyaDurumu: 'Açık (Durdurulmuş: Takibe İtiraz)',
    tutar: 22000,
    aciklama: 'Kredi borcu takibi - İtiraz var'
  },
  {
    id: 8,
    tarafRolu: 'Alacaklı',
    mahkemeAdi: 'Düzce İcra Dairesi',
    esasNo: '2021/8396',
    dosyaTuru: 'İcra Dosyası',
    acilisTarihi: '2021-06-18',
    dosyaDurumu: 'Açık',
    tutar: 15000,
    aciklama: 'Alacak takibi'
  },
  {
    id: 9,
    tarafRolu: 'Alacaklı',
    mahkemeAdi: 'Karşıyaka 2. İcra Dairesi',
    esasNo: '2020/3014',
    dosyaTuru: 'İcra Dosyası',
    acilisTarihi: '2020-09-17',
    dosyaDurumu: 'Açık (Durdurulmuş: Takibe İtiraz)',
    tutar: 8500,
    aciklama: 'Alacak takibi - İtiraz var'
  },
  {
    id: 10,
    tarafRolu: 'Borçlu',
    mahkemeAdi: 'İstanbul Anadolu 9. İcra Dairesi',
    esasNo: '2019/7114',
    dosyaTuru: 'İcra Dosyası',
    acilisTarihi: '2019-02-14',
    dosyaDurumu: 'Kapalı (Haricen Tahsil)',
    tutar: 12000,
    aciklama: 'Kredi borcu takibi - Haricen tahsil edildi'
  },
];

const IcraDosyalariPage: React.FC = () => {
  const [searchTerm, setSearchTerm] = React.useState('');
  const [selectedTarafRolu, setSelectedTarafRolu] = React.useState('');
  const [selectedDosyaTuru, setSelectedDosyaTuru] = React.useState('');
  const [selectedDosyaDurumu, setSelectedDosyaDurumu] = React.useState('');
  const [dateRange, setDateRange] = React.useState({ start: '', end: '' });
  const [currentPage, setCurrentPage] = React.useState(1);
  const [itemsPerPage, setItemsPerPage] = React.useState(10);

  // Filtreleme fonksiyonu
  const filteredData = useMemo(() => {
    return icraVerileri.filter(dosya => {
      const matchesSearch = 
        dosya.mahkemeAdi.toLowerCase().includes(searchTerm.toLowerCase()) ||
        dosya.esasNo.toLowerCase().includes(searchTerm.toLowerCase()) ||
        dosya.aciklama?.toLowerCase().includes(searchTerm.toLowerCase());
      
      const matchesTarafRolu = !selectedTarafRolu || dosya.tarafRolu === selectedTarafRolu;
      const matchesDosyaTuru = !selectedDosyaTuru || dosya.dosyaTuru === selectedDosyaTuru;
      const matchesDosyaDurumu = !selectedDosyaDurumu || dosya.dosyaDurumu === selectedDosyaDurumu;
      
      const matchesDateRange = !dateRange.start || !dateRange.end || 
        (dosya.acilisTarihi >= dateRange.start && dosya.acilisTarihi <= dateRange.end);
      
      return matchesSearch && matchesTarafRolu && matchesDosyaTuru && matchesDosyaDurumu && matchesDateRange;
    });
  }, [searchTerm, selectedTarafRolu, selectedDosyaTuru, selectedDosyaDurumu, dateRange]);

  // Sayfalama
  const totalPages = Math.ceil(filteredData.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentData = filteredData.slice(startIndex, endIndex);

  // Benzersiz değerler için
  const tarafRolleri = Array.from(new Set(icraVerileri.map(d => d.tarafRolu)));
  const dosyaTurleri = Array.from(new Set(icraVerileri.map(d => d.dosyaTuru)));
  const dosyaDurumlari = Array.from(new Set(icraVerileri.map(d => d.dosyaDurumu)));

  const handleView = (dosya: IcraDosyasi) => {
    alert(`Dosya Görüntüleniyor: ${dosya.mahkemeAdi} - ${dosya.esasNo}`);
  };

  const handleEdit = (dosya: IcraDosyasi) => {
    alert(`Dosya Düzenleniyor: ${dosya.mahkemeAdi} - ${dosya.esasNo}`);
  };

  const handleDelete = (dosya: IcraDosyasi) => {
    if (confirm(`"${dosya.mahkemeAdi} - ${dosya.esasNo}" dosyasını silmek istediğinizden emin misiniz?`)) {
      alert(`Dosya Silindi: ${dosya.mahkemeAdi} - ${dosya.esasNo}`);
    }
  };

  const getStatusColor = (durum: string) => {
    if (durum.toLowerCase().includes('açık')) {
      return 'bg-green-100 text-green-800';
    } else if (durum.toLowerCase().includes('kapalı')) {
      return 'bg-red-100 text-red-800';
    } else if (durum.toLowerCase().includes('durdurulmuş')) {
      return 'bg-yellow-100 text-yellow-800';
    }
    return 'bg-gray-100 text-gray-800';
  };

  return (
    <div className="space-y-6">
      {/* Başlık */}
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-800">İcra Dosyaları</h1>
        <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center gap-2">
          <FiEdit className="w-4 h-4" />
          Yeni Dosya Ekle
        </button>
      </div>

      {/* Filtreler */}
      <div className="bg-gray-800 p-6 rounded-lg shadow-md">
        <h2 className="text-lg font-semibold mb-4 text-white">Filtreler</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {/* Arama */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">Arama</label>
            <input
              type="text"
              placeholder="Mahkeme, esas no veya açıklama ara..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full p-2 border border-gray-600 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-gray-700 text-white placeholder-gray-400"
            />
          </div>

          {/* Taraf Rolü */}
          <div>
            <label className="block text-sm font-medium text-gray-300 mb-2">Taraf Rolü</label>
            <select
              value={selectedTarafRolu}
              onChange={(e) => setSelectedTarafRolu(e.target.value)}
              className="w-full p-2 border border-gray-600 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-gray-700 text-white"
            >
              <option value="">Tümü</option>
              {tarafRolleri.map(rol => (
                <option key={rol} value={rol}>{rol}</option>
              ))}
            </select>
          </div>

          {/* Dosya Türü */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Dosya Türü</label>
            <select
              value={selectedDosyaTuru}
              onChange={(e) => setSelectedDosyaTuru(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="">Tümü</option>
              {dosyaTurleri.map(tur => (
                <option key={tur} value={tur}>{tur}</option>
              ))}
            </select>
          </div>

          {/* Dosya Durumu */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Dosya Durumu</label>
            <select
              value={selectedDosyaDurumu}
              onChange={(e) => setSelectedDosyaDurumu(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="">Tümü</option>
              {dosyaDurumlari.map(durum => (
                <option key={durum} value={durum}>{durum}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Tarih Aralığı */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Başlangıç Tarihi</label>
            <input
              type="date"
              value={dateRange.start}
              onChange={(e) => setDateRange(prev => ({ ...prev, start: e.target.value }))}
              className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Bitiş Tarihi</label>
            <input
              type="date"
              value={dateRange.end}
              onChange={(e) => setDateRange(prev => ({ ...prev, end: e.target.value }))}
              className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>
        </div>

        {/* Filtreleri Temizle */}
        <div className="mt-4">
          <button
            onClick={() => {
              setSearchTerm('');
              setSelectedTarafRolu('');
              setSelectedDosyaTuru('');
              setSelectedDosyaDurumu('');
              setDateRange({ start: '', end: '' });
            }}
            className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-md"
          >
            Filtreleri Temizle
          </button>
        </div>
      </div>

      {/* Tablo */}
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Taraf Rolü
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Mahkeme Adı
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Esas No
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Dosya Türü
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Açılış Tarihi
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Tutar (₺)
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Dosya Durumu
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  İşlemler
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {currentData.map((dosya) => (
                <tr key={dosya.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {dosya.tarafRolu}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {dosya.mahkemeAdi}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 font-mono">
                    {dosya.esasNo}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {dosya.dosyaTuru}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {new Date(dosya.acilisTarihi).toLocaleDateString('tr-TR')}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {dosya.tutar?.toLocaleString('tr-TR') || '-'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(dosya.dosyaDurumu)}`}>
                      {dosya.dosyaDurumu}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleView(dosya)}
                        className="text-blue-600 hover:text-blue-900"
                        title="Görüntüle"
                      >
                        <FiEye className="w-4 h-4" />
                      </button>
                      <button
                        onClick={() => handleEdit(dosya)}
                        className="text-green-600 hover:text-green-900"
                        title="Düzenle"
                      >
                        <FiEdit className="w-4 h-4" />
                      </button>
                      <button
                        onClick={() => handleDelete(dosya)}
                        className="text-red-600 hover:text-red-900"
                        title="Sil"
                      >
                        <FiTrash2 className="w-4 h-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Sayfalama */}
        <div className="bg-white px-4 py-3 flex items-center justify-between border-t border-gray-200 sm:px-6">
          <div className="flex-1 flex justify-between sm:hidden">
            <button
              onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
              className="relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50"
            >
              Önceki
            </button>
            <button
              onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
              disabled={currentPage === totalPages}
              className="ml-3 relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50"
            >
              Sonraki
            </button>
          </div>
          <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
            <div>
              <p className="text-sm text-gray-700">
                <span className="font-medium">{(currentPage - 1) * itemsPerPage + 1}</span> -{' '}
                <span className="font-medium">{Math.min(currentPage * itemsPerPage, filteredData.length)}</span> /{' '}
                <span className="font-medium">{filteredData.length}</span> sonuç
              </p>
            </div>
            <div>
              <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
                <button
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                  className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50"
                >
                  Önceki
                </button>
                {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                  <button
                    key={page}
                    onClick={() => setCurrentPage(page)}
                    className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                      currentPage === page
                        ? 'z-10 bg-blue-50 border-blue-500 text-blue-600'
                        : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50'
                    }`}
                  >
                    {page}
                  </button>
                ))}
                <button
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                  className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 disabled:opacity-50"
                >
                  Sonraki
                </button>
              </nav>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default IcraDosyalariPage;
